clear all; close all;A=[2 1;1 20;1 5;4 1;1 40;3 30];y=[0;0;0;1;1;1];x1=A(:,1);x2=A(:,2);alpha=.001;theta1 = 0;theta2 = 0;theta0 = 0;for i = 1 : 100000thetatx=theta1*x1 + theta2*x2 +theta0;p=exp(-1*thetatx);t=1./(1+p);temp1 = theta1-alpha*sum((t-y).*x1);temp2 = theta2-alpha*sum((t-y).*x2);temp3 = theta0-alpha*sum(t-y);theta1 = temp1;theta2 = temp2;theta0 = temp3;thetatx=theta1*x1 + theta2*x2 +theta0;p=exp(-1*thetatx);t=1./(1+p);HMN= -1*sum(y.*log(t)+(1-y).*log(1-t));O(i)=HMN;endfprintf('\n\nSize %f \n', size(O));%disp(O);plot(O);A=[2 1;1 20;1 5;4 1;1 40;3 30];yy=-(theta1*x1+theta0)/theta2;plot(A(1:3,1), A(1:3,2), 'r+', A(4:6,1), A(4:6,2), 'bo', x1, yy, 'g-','linewidth',2);%axis([0.5 1 -1 1]);  % change axis scalexbounds = xlim()set(gca, 'xtick', xbounds(1):0.5:xbounds(2))