clear all; close all;pkg load statistics;train = dlmread('wine_train.txt', ",");test = dlmread('wine_test.txt', ",");label_train  = train(:,columns(train));label_test  = test(:,columns(test));train(:,columns(train)) = [];test(:,columns(test)) = [];train = normalize(train);test = normalize(test);predict = myKnn(train, test, label_train, 10);[predict' label_test];size(predict');size(label_test);s = rows(test);E = sum(1-(predict'==label_test))./s;fprintf('\n Error %f\n', E);