clear all; close all;[x1, x2, x3, x4, x5, x6, x7, x8, y] = textread('yeast_train.txt', '%f,%f,%f,%f,%f,%f,%f,%f,%s');train = [x1 x2 x3 x4 x5 x6 x7 x8];u_label = unique(y);label_train  = str2num4label(y, u_label);[x1, x2, x3, x4, x5, x6, x7, x8, y] = textread('yeast_test.txt', '%f,%f,%f,%f,%f,%f,%f,%f,%s');test = [x1 x2 x3 x4 x5 x6 x7 x8];label_test  = str2num4label(y, u_label);train = normalize(train);test = normalize(test);s = rows(test);for k = 1:20  predict = myKnn(train, test, label_train, k);    %freq_ans = mode(predict);  [predict' label_test];    E = sum(1-(predict'==label_test))./s;  fprintf('\n K %d Error %f\n', k, E);endfor